import React from 'react';

function LoginForm() {
    const handleSubmit = (event) => {
        event.preventDefault();
        // Handle login logic here
    };

    const containerStyle = {
        background: 'linear-gradient(rgba(0,0,0,0.8), rgba(0,0,0,0.85))',
        color: 'white',
        fontFamily: '"Helvetica Neue", Helvetica, Arial, sans-serif',
        padding: '20px',
        borderRadius: '5px',
        maxWidth: '320px',
        margin: '100px auto',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
    };

    const inputStyle = {
        marginBottom: '15px',
        width: '100%',
        padding: '10px',
        borderRadius: '4px',
        border: '1px solid #333',
        backgroundColor: '#333',
        color: 'white'
    };

    const buttonStyle = {
        backgroundColor: '#1DB954',
        color: 'white',
        padding: '10px 20px',
        border: 'none',
        borderRadius: '20px',
        cursor: 'pointer',
        width: '100%'
    };

    const labelStyle = {
        alignSelf: 'flex-start',
        marginBottom: '5px',
    };

    return (
        <div style={{ background: '#121212', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <div style={containerStyle}>
                <h2 style={{ color: '#1DB954' }}>Spotify</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor="username" style={labelStyle}>Username:</label>
                        <input type="text" id="username" name="username" required style={inputStyle} />
                    </div>
                    <div>
                        <label htmlFor="password" style={labelStyle}>Password:</label>
                        <input type="password" id="password" name="password" required style={inputStyle} />
                    </div>
                    <div style={{ marginBottom: '15px', alignSelf: 'flex-start' }}>
                        <input type="checkbox" id="remember-me" />
                        <label htmlFor="remember-me" style={{ marginLeft: '5px', userSelect: 'none' }}>
                            Remember me
                        </label>
                    </div>
                    <button type="submit" style={buttonStyle}>Login</button>
                </form>
            </div>
        </div>
    );
}

export default LoginForm;
