import React from 'react';
import styles from './LoginForm.module.css';

import styled from 'styled-components';

// Styled components
const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 300px;
  margin: auto;
  background-color: #121212; /* Dark background like Spotify */
  padding: 20px;
  border-radius: 4px;
`;

const Label = styled.label`
  margin-bottom: 5px;
  color: white;
`;

const Input = styled.input`
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #333;
  background-color: #333;
  color: white;
`;

const Button = styled.button`
  background-color: #1DB954;
  color: white;
  padding: 10px 20px;
  border: none;
  border-radius: 20px;
  cursor: pointer;
  margin-top: 10px; /* To give space between the checkbox and button */
`;

// Checkbox styled component
const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px; /* Added margin for spacing */
`;

const RememberMeCheckbox = styled.input`
  margin-right: 5px;
`;

function LoginForm() {
    const handleSubmit = (event) => {
        event.preventDefault();
        // Handle login logic here
    };

    return (
        <div className={styles.formContainer}>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="username" className={styles.label}>Username:</label>
                    <input type="text" id="username" name="username" required className={styles.input} />
                </div>
                <div>
                    <label htmlFor="password" className={styles.label}>Password:</label>
                    <input type="password" id="password" name="password" required className={styles.input} />
                </div>
                <div>
                    <input type="checkbox" id="remember-me" />
                    <label htmlFor="remember-me" className={styles.label} style={{ display: 'inline-block', marginLeft: '5px' }}>
                        Remember me
                    </label>
                </div>
                <button type="submit" className={styles.button}>Login</button>
            </form>
        </div>
    );
}

export default LoginForm;
