import React from 'react';

function LoginForm() {
    const handleSubmit = (event) => {
        event.preventDefault();
        // Handle login logic here
    };

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        width: '300px', // Adjust the width as needed
        margin: 'auto'
    };

    const labelStyle = {
        marginBottom: '5px',
        color: 'white'
    };

    const inputStyle = {
        marginBottom: '10px',
        padding: '10px',
        borderRadius: '4px',
        border: '1px solid #333',
        backgroundColor: '#333',
        color: 'white'
    };

    const buttonStyle = {
        backgroundColor: '#1DB954',
        color: 'white',
        padding: '10px 20px',
        border: 'none',
        borderRadius: '20px',
        cursor: 'pointer'
    };

    return (
        <form onSubmit={handleSubmit} style={formStyle}>
            <div>
                <label htmlFor="username" style={labelStyle}>Username:</label>
                <input type="text" id="username" name="username" required style={inputStyle} />
            </div>
            <div>
                <label htmlFor="password" style={labelStyle}>Password:</label>
                <input type="password" id="password" name="password" required style={inputStyle} />
            </div>
            <div style={{ marginBottom: '10px' }}>
                <input type="checkbox" id="remember-me" />
                <label htmlFor="remember-me" style={{ ...labelStyle, display: 'inline-block', marginLeft: '5px' }}>
                    Remember me
                </label>
            </div>
            <button type="submit" style={buttonStyle}>Login</button>
        </form>
    );
}

export default LoginForm;
